$(function () {
    $(document).ready(function () {
        $('.doc_link_ajax').on('click', function () {
            var ajaxQuery = $(this).attr("queryVal");
            var docName = $(this).text();
            $('#tt_doc_name').text(docName);
            $.ajax({
                type: 'POST',
                url: ajaxPathUrl,
                data: {
                    myQw: ajaxQuery
                },
                success: function (data) {
                    var queryArr = [];
                    queryArr = JSON.parse(data);
                    $.each(queryArr, function (key, value) {
                        $('#tt_hourstart' + key).text(value.start_hour);
                        $('#tt_minutestart' + key).text(value.start_minute);
                        $('#tt_hourend' + key).text(value.end_hour);
                        $('#tt_minuteend' + key).text(value.end_minute);
                    });

                }
            });
        });
    });
});